package oc.wh40k.armies;import oc.BuildaHQ;import oc.BuildaVater;import oc.ChooserGruppe;public class VOLKEldar extends BuildaVater {	public VOLKEldar() {		reflectionKennung = "EL";		reflectionKennungAllies = "EL";		String[] HQeinträge = {"", "Autarch", "Runenprophet", "Runenleser", "", "Der Avatar des Khaine", "Prinz Yriel von Iyanden", "Eldrad Ulthran", "Phönixkönig", "", "[Forgeworld] Wraithseer", "[Forgeworld] Irillyth", "[Forgeworld] Bel-Annath"};		String[] Eliteeinträge = {"", "Skorpionkrieger", "Feuerdrachen", "Phantomdroiden", "Banshees", "Harlequintrupp"};		String[] Standardeinträge = {"", "Asuryans Jäger", "Ranger", "Gardisten", "Gardistenjetbikeschwadron", "Standard Phantomdroiden"};		String[] Sturmeinträge = {"", "Speere des Khaine", "Warpspinnen", "Kriegsfalken", "Vyperschwadron", "", "[Forgeworld] Shadow Spectres", "[Forgeworld] Hornet Squadron", "[Forgeworld] Wasp Assault Walker Squadron", "[Forgeworld] Nightwing Interceptor"};		String[] Unterstützungeinträge = {            "", "Unterstützungswaffenbatterie", "Schwarze Khaindar", "Phantomlord", "Kampfläuferschwadron", "Falcon", "Illum Zar", "Schattenspinne",             "", "[Forgeworld] Fire Storm", "[Forgeworld] Phoenix Bomber", "[Forgeworld] Night Spinner", "[Forgeworld] Warp Hunter"        };		String[] Befestigungen = {"", "Aegis-Verteidigungslinie", "Himmelsschild-Landeplattform", "Imperiale Bastion", "Festung der Erlösung"};		String[] ApoEinheiten = {            "", "Kuanor-Jagdtitan",            "", "[Forgeworld] Cobra", "[Forgeworld] Lynx", "[Forgeworld] Scorpion",            "", "[Forgeworld] Phantom Titan",            "", "[Forgeworld] Vampire Hunter", "[Forgeworld] Vampire Raider"        };		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 1, HQeinträge));		cnt += CHOOSERGRUPPEN_X_ABSTAND;		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 2, Eliteeinträge));		cnt += CHOOSERGRUPPEN_X_ABSTAND;		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 3, Standardeinträge));		cnt += CHOOSERGRUPPEN_X_ABSTAND;		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 4, Sturmeinträge));		cnt += CHOOSERGRUPPEN_X_ABSTAND;		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 5, Unterstützungeinträge));		cnt += CHOOSERGRUPPEN_X_ABSTAND;		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 6, Befestigungen));        if (oc.OnlineCodex.getInstance().isApo()) cnt += CHOOSERGRUPPEN_X_ABSTAND;        if (oc.OnlineCodex.getInstance().isApo()) adden(new ChooserGruppe(reflectionKennung, cnt, 0, 7, ApoEinheiten));		cnt += 480;		nameDerArtDerArmee = "Eldar";		nameDerArtDerArmeeDekliniert = "Eldar";		textAreaUeberschrift = BuildaHQ.translate("Meine") + " Eldar " + BuildaHQ.translate("Armee");		noAllies.clear();				noAllies.add("EL");		noAllies.add("CD");		noAllies.add("CM");		noAllies.add("NE");		noAllies.add("RH");		noAllies.add("TL");		noAllies.add("TY");				complete();	}	@Override	protected void finalize() {		System.out.println("Object gelöscht in " + nameDerArtDerArmee + "Builda !!");	}}