package oc.whfb.armies;import oc.BuildaHQ;import oc.BuildaVater;import oc.ChooserGruppe;public class VOLKZwerge extends BuildaVater {	public VOLKZwerge() {		reflectionKennung = "ZW";				String[] Kommandanten = {"", BuildaHQ.translate("König"), BuildaHQ.translate("Runenmeister"), BuildaHQ.translate("Dämonenslayer"), "", BuildaHQ.translate("Thorgrim Grollbart"), BuildaHQ.translate("Thorek Eisenstirn"), "Grombrindal, der weiße Zwerg"};		String[] Helden = {"", BuildaHQ.translate("Thain"), BuildaHQ.translate("Runenschmied"), BuildaHQ.translate("Meistermaschinist"), BuildaHQ.translate("Drachenslayer"), "", BuildaHQ.translate("Josef Bugman")};		String[] Kern = {"", BuildaHQ.translate("Klankrieger"), BuildaHQ.translate("Langbärte"), BuildaHQ.translate("Armbrustschützen"), BuildaHQ.translate("Musketenschützen")};		String[] Elite = {"", BuildaHQ.translate("Hammerträger"), BuildaHQ.translate("Kanone"), BuildaHQ.translate("Speerschleuder"), BuildaHQ.translate("Bergwerker"), BuildaHQ.translate("Eisenbrecher"), BuildaHQ.translate("Grollschleuder"), BuildaHQ.translate("Slayer")};		String[] Selten = {"", BuildaHQ.translate("Orgelkanone"), BuildaHQ.translate("Flammenkanone"), BuildaHQ.translate("Gyrokopter")};		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 1, Kommandanten));		cnt += CHOOSERGRUPPEN_X_ABSTAND;		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 2, Helden));		cnt += CHOOSERGRUPPEN_X_ABSTAND;		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 3, Kern));		cnt += CHOOSERGRUPPEN_X_ABSTAND;		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 4, Elite));		cnt += CHOOSERGRUPPEN_X_ABSTAND;		adden(new ChooserGruppe(reflectionKennung, cnt, 0, 5, Selten));		cnt += 480;		nameDerArtDerArmee = BuildaHQ.translate("Zwerge");		nameDerArtDerArmeeDekliniert = BuildaHQ.translate("Zwerge");		textAreaUeberschrift = BuildaHQ.translate("Zwerge");		complete();	}	@Override	protected void finalize() {		 	}}