package oc.whfb.units.vf;import oc.BuildaHQ;import oc.OptionsGruppeEintrag;import oc.OptionsUpgradeGruppe;import oc.RuestkammerVater;public class VFNekromantKammer extends RuestkammerVater {	OptionsUpgradeGruppe o1;	OptionsUpgradeGruppe o2;	public VFNekromantKammer() {		grundkosten = 0;	}	@Override	public void initButtons(boolean... defaults) {		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Nachtmahr"), 15));		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Leichenkarren"), 75));		add(o1 = new OptionsUpgradeGruppe(ID, randAbstand, cnt, "", ogE));		seperator();		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Hexenfeuer"), 25));		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Unheiliger Sog"), 25));		add(o2 = new OptionsUpgradeGruppe(ID, randAbstand, cnt, "", ogE));		setUeberschrift(BuildaHQ.translate("Ausrüstung"));		sizeSetzen(100, 100, 285, KAMMER_HOEHE + cnt + 10);	}	@Override	public void refreshen() {		if(!o1.isSelected()) o1.setSelected(0, true);		o2.setAktiv(o1.isSelected(BuildaHQ.translate("Leichenkarren")));	}}	