package oc.whfb.units.vf;import oc.BuildaHQ;import oc.CommonMagicItems;import oc.OptionsGruppeEintrag;import oc.OptionsZaehlerGruppe;import oc.RuestkammerVater;public class VFManfredKammer extends RuestkammerVater {	OptionsZaehlerGruppe oz1;	boolean isFürst = false;	public VFManfredKammer() {		grundkosten = 0;	}	@Override	public void initButtons(boolean... defaults) {				isFürst = defaults[0];		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Schädelstab"), 65));		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Stab d. Verdammnis"), BuildaHQ.translate("Stab der Verdammnis"), 40));		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Buch des Arkhan"), 35));		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Szepter des Noirot"), 25));		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Blutrotes Juwel"), BuildaHQ.translate("Blutrotes Juwel von Lahmia"), 20));		ogE.addElement(new OptionsGruppeEintrag(BuildaHQ.translate("Schwarzer Periapt"), 15));				CommonMagicItems.addCommonArcane(ogE, 100, 35, 20, 25);				add(oz1 = new OptionsZaehlerGruppe(ID, randAbstand, cnt, "", ogE, isFürst ? 2 : 1));		sizeSetzen(100, 100, 285, KAMMER_HOEHE + cnt + 10);	}	@Override	public void refreshen() {		oz1.setButtonMaxAnzahl(1);	}}	